  CCGetWorldMatrixFull(matWorld, matWorldIT);
  vec3 worldNormal = normalize((matWorldIT * vec4(In.normal, 0.0)).xyz);
  vec4 position = In.position;
  worldPos = (matWorld * position).xyz;
  v_uv = worldPos.xz * terrainParam.x;
  vec3 col = texture(mainTexture, v_uv).xyz;
  worldPos.y = terrainParam.y * col.x;
